  let [defaultOption,defaultOptionChanged] = useState('');
  const onSelect = () => {
    console.log("Chnged");
  };
  axios
    .get("https://cdn-api.co-vin.in/api/v2/admin/location/states")
    .then((response) => {
      statechanged(response.data.states.map((state)=>{
return state.state_name;
      }));
      defaultOptionChanged(states[0].state_name);
      console.log(response)
    })
